import React, { useEffect, useState } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import styled from "styled-components";
import WishBookItemInfo from "./WishBookItemInfo";
import WishBookItemImg from "./WishBookItemImg";
import media from "styles/media";
import { useRecoilState, useRecoilValue } from "recoil";
import { bookDataState, filteredBookSelector } from "recoil/books";

export interface ResultProps {
  result: {
    authors: string[];
    contents: string;
    datetime: string;
    isbn: string;
    price: string;
    publisher: string;
    sale_price: string;
    status: string;
    thumbnail: string;
    title: string;
    translators: string[];
    url: string;
  };
}
function WishBookItem({ result }: ResultProps): JSX.Element {
  const {
    authors,
    contents,
    datetime,
    isbn,
    price,
    publisher,
    sale_price,
    status,
    thumbnail,
    title,
    translators,
    url,
  } = result;
  let bookCode = isbn.split(" ").join("");

  const [book, setBook] = useRecoilState(bookDataState);

  // 최근 검색기록
  const [watch, setWatch] = useRecoilState(filteredBookSelector(bookCode));

  const isWatched = () => {
    if(book)
    setWatch(!watch);
  };

  useEffect(() => {
    const filteredBooks = book.filter((v) => v.watch === true);
    console.log(filteredBooks);
  }, [book]); // book 상태가 변경될 때마다 실행됩니다.

  return (
    <>
      <Wrapper key={result.isbn}>
        <SLink
          to={`${bookCode}`}
          state={{
            result: result,
          }}
          onClick={isWatched}
        >
          <WishBookItemImg title={title} thumbnail={thumbnail} page="main" />
          <WishBookItemInfo
            authors={authors}
            contents={contents}
            datetime={datetime}
            isbn={isbn}
            price={price}
            publisher={publisher}
            sale_price={sale_price}
            status={status}
            thumbnail={thumbnail}
            title={title}
            translators={translators}
            url={url}
            page="main"
          ></WishBookItemInfo>
        </SLink>
      </Wrapper>
    </>
  );
}

export default WishBookItem;

const Wrapper = styled.li`
  width: calc(20% - 15px);
  background-color: var(--inner-bg-color);
  border-radius: 10px;
  ${media.medium`
    width: calc(33% - 15px)
  `}
  ${media.small`
    width: calc(50% - 15px)
  `}
`;

const SLink = styled(Link)`
  position: relative;
  width: 100%;
`;
