import { watch } from "fs";
import { selector, selectorFamily, useRecoilState } from "recoil";
import { bookmarkState } from "recoil/atoms/atoms";

export const filteredBookmarkSelector = selectorFamily({
  key: "filteredBookmark",
  get:
    (param: []) =>
    ({ get }) => {
      // 로컬스토리지에 있는 북마크 책
      const list = [get(bookmarkState)];
      let newList = [...list];
    },
});

export const filteredDetailState = selectorFamily({
  key: "filteredDetailState",
  get:
    (param: []) =>
    ({ get }) => {
      const list = [get(useRecoilValue(bookData(searchKeword(search))))];
    },
});
