import { selector, selectorFamily } from "recoil";
import axios, { AxiosError } from "axios";
import { bookState, searchInfoState } from "recoil/atoms";
import { watch } from "fs";
import { BookDTO } from "components/types/searchType";

// kakao API 호출
const KAKAO_API_KEY = process.env.REACT_APP_KAKAO_API_KEY;

export const bookData = selectorFamily({
  key: "bookData",
  get:
    (search) =>
    async ({ get }) => {
      const searchValue = search;
      let bookValue = get(bookState);
      // API 호출
      try {
        const res = await axios({
          method: "get",
          baseURL: "https://dapi.kakao.com/v3/search/book",
          headers: {
            Authorization: "KakaoAK " + KAKAO_API_KEY,
          },
          params: {
            query: search,
            size: 50,
            target: ["title", "person"],
          },
        });
        let newBooks = res.data.documents;
        bookValue = newBooks.map((el: BookDTO[]) => {
          return { ...el, watch: watch };
        });
        console.log(searchValue, bookValue);
        return res.data.documents;
      } catch (error) {
        if (error instanceof AxiosError) {
          if (error.response?.status === 400) {
            console.log(error);
          }
        }
      }
    },
});
