import axios, { AxiosError } from "axios";
import { BookDTO } from "components/types/searchType";
import { atom, selector, selectorFamily } from "recoil";
import { recoilPersist } from "recoil-persist";

const { persistAtom } = recoilPersist();

// kakao API 호출
const KAKAO_API_KEY = process.env.REACT_APP_KAKAO_API_KEY;

export const bookData = selectorFamily({
  key: "bookData",
  get:
    (search) =>
    async ({ get }) => {
      // API 호출
      try {
        const res = await axios({
          method: "get",
          baseURL: "https://dapi.kakao.com/v3/search/book",
          headers: {
            Authorization: "KakaoAK " + KAKAO_API_KEY,
          },
          params: {
            query: search,
            size: 50,
            target: ["title", "person"],
          },
        });
        let newData = res.data.documents.map((el: any) => {
          return { ...el, watch: false };
        });
        return newData;
      } catch (error) {
        if (error instanceof AxiosError) {
          if (error.response?.status === 400) {
            console.log(error);
          }
        }
      }
    },
});

// 검색어
export const searchInfoState = atom({
  key: "searchInfoState",
  default: "",
});

// 검색한 책
export const bookDataState = atom<BookDTO[]>({
  key: "bookDataState",
  default: [],
  effects_UNSTABLE: [persistAtom],
});

// 상세보기 책
export const bookDetailState = atom<BookDTO[] | undefined>({
  key: "bookDetailState",
  default: [],
  effects_UNSTABLE: [persistAtom],
});

// 북마크한 책
export const bookmarkState = atom({
  key: "bookmark",
  default: [],
  effects_UNSTABLE: [persistAtom],
});

export const filteredBookSelector = selectorFamily<boolean | undefined, string>(
  {
    key: "filteredBookSelector",
    get:
      (bookCode) =>
      ({ get, set }) => {
        const bookState = get(bookDataState);
        const findItem = bookState?.find((item) => {
          let isbn = item.isbn;
          isbn = isbn.split(" ").join("");
          return isbn == bookCode;
        });
        set(bookDetailState, findItem);
        console.log(detailState);
        return findItem?.watch;
      },
    set:
      (bookCode) =>
      ({ set, get }, newValue) => {
        const bookState = get(bookDataState);

        const updatedData = bookState?.map((item: any) => {
          let isbn = item.isbn;
          isbn = isbn.split(" ").join("");
          if (isbn == bookCode) {
            return { ...item, watch: newValue };
          }
          return item;
        });
        set(bookDataState, updatedData);
      },
  }
);
