import { faBookmark } from "@fortawesome/free-solid-svg-icons";
import SearchBar from "components/Main/SearchBar";
import IconButton from "components/common/Button/IconButton";
import { BookDTO } from "components/types/searchType";
import { useEffect, useMemo, useState } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import { selector, useRecoilState, useRecoilValue } from "recoil";
import {
  bookDataSelectorFamily,
  filteredBookSelector,
  bookDataState,
  searchInfoState,
  detailBookSelector,
  bookDetailState,
} from "recoil/books";
import styled from "styled-components";
import FootPage from "./FootPage";
import WishBookItem from "components/Book/WishBookItem";
import { watch } from "fs";

function MainPage(): JSX.Element {
  // 검색어 초기값 설정
  const item = ["이병률", "조예은", "정세랑", "김영하"];
  const randomKeyword = (min: number, max: number) => {
    return Math.trunc(Math.random() * (max - min + 1));
  };

  const random = useMemo(() => randomKeyword(1, item.length), []);
  const [search, setSearch] = useRecoilState(searchInfoState);

  const searchKeword = (search: any) => {
    if (!search) {
      return item[random];
    } else {
      return search;
    }
    return true;
  };
  // 페이지 이동
  const navigate = useNavigate();
  const goToWishBookPage = () => navigate("/wishbook");

  // 검색한 결과값
  const bookSelector = useRecoilValue(
    bookDataSelectorFamily(searchKeword(search))
  );
  const [book, setBook] = useRecoilState(bookDataState);
  const [detail, setDetail] = useRecoilState(bookDetailState);

  const detailBook = useRecoilValue(detailBookSelector);

  useEffect(() => {
    if (detailBook !== undefined) {
      setDetail(detailBook);
    }
  }, [detailBook]);

  // 1. 애초에 api 로 받아온 데이터에 watch = false 라는 속성을 추가한다.
  // 2. 만약 눌러서 탐색을 한다면? 그 데이터는 따로 저장을 한다.
  // 3. 검색결과 중에서 watch 가 true 인것만 따로 보관

  useEffect(() => {
    setBook(bookSelector);
  }, [bookSelector, setBook]);

  const handleWatchToggle = (bookCode: string) => {
    const currentBook = book.find(
      (b) => b.isbn.split(" ").join("") === bookCode
    );
    if (currentBook && currentBook.watch) {
      return;
    }
    // watch 값을 토글
    setBook((prevBooks) =>
      prevBooks.map((b) => {
        return b.isbn.split(" ").join("") === bookCode
          ? { ...b, watch: !b.watch }
          : b;
      })
    );
  };

  console.log(book);
  return (
    <>
      <Header>
        <Nav>
          <IconButton
            fontSize="20px"
            icon={faBookmark}
            title="위시리스트"
            onClick={goToWishBookPage}
            color={"#0047ab"}
          />
        </Nav>
        <Logo>
          <Link to="/">
            <LogoImg
              src={require("assets/images/logo.png")}
              alt="WISHREAD 로고"
            />
          </Link>
        </Logo>
        <SearchBar />
      </Header>
      {!search ? (
        <>
          <Title>최근 검색한 책</Title>
          <WishBookList>
            {detail
              .filter((v) => v.watch)
              .map((result) => (
                <WishBookItem
                  result={result}
                  key={result.isbn.split(" ").join("")}
                  onWatchToggle={() =>
                    handleWatchToggle(result.isbn.split(" ").join(""))
                  }
                />
              ))}
          </WishBookList>
        </>
      ) : (
        ``
      )}

      {!search ? <Title>추천책</Title> : <Title>검색결과</Title>}
      <WishBookList>
        {bookSelector.map((result: BookDTO) => (
          <WishBookItem
            result={result}
            key={result.isbn.split(" ").join("")}
            onWatchToggle={() =>
              handleWatchToggle(result.isbn.split(" ").join(""))
            }
          />
        ))}
      </WishBookList>
      <FootPage page="main" />
    </>
  );
}
export default MainPage;

const Header = styled.header`
  position: sticky;
  z-index: 9999;
  top: 0;
  padding: 10px var(--pd-content);
  background-color: var(--inner-bg-color);
`;

const Nav = styled.nav`
  display: flex;
  justify-content: flex-end;
  align-items: center;
`;
const Logo = styled.h1`
  display: flex;
  width: 200px;
  height: auto;
`;
const LogoImg = styled.img`
  display: block;
  width: 100%;
`;
const WishBookList = styled.ul`
  display: flex;
  flex-wrap: wrap;
  gap: 15px;
  row-gap: 15px;
  padding: 30px;
`;

export const Title = styled.h2`
  padding: 2rem var(--pd-content) 0;
  font-size: 2rem;
`;
