import axios, { AxiosError } from "axios";
import { BookDTO } from "components/types/searchType";
import { DefaultValue, atom, selector, selectorFamily } from "recoil";
import { recoilPersist } from "recoil-persist";

const { persistAtom } = recoilPersist();

// kakao API 호출
const KAKAO_API_KEY = process.env.REACT_APP_KAKAO_API_KEY;

// 입력한 검색어
export const searchInfoState = atom({
  key: "searchInfoState",
  default: "",
});

// 하나의 책이 가진 고유의 값(isMarked와 isWatched 값 관리) atom 으로 전역적으로 상태관리
export const bookDetailState = atom({
  key: "bookDetailState",
  default: [],
  effects_UNSTABLE: [persistAtom],
});

// 검색 api 호출
export const searchApiSelector = selectorFamily({
  key: "searchApiSelector",
  get:
    (search) =>
    async ({ get }) => {
      // API 호출
      try {
        const res = await axios({
          method: "get",
          baseURL: "https://dapi.kakao.com/v3/search/book",
          headers: {
            Authorization: "KakaoAK " + KAKAO_API_KEY,
          },
          params: {
            query: search,
            size: 50,
            target: ["title", "person"],
          },
        });
        return res.data.documents;
      } catch (error) {
        if (error instanceof AxiosError) {
          if (error.response?.status === 400) {
            console.log(error);
          }
        }
      }
    },
});

export const detailBookSelector = selectorFamily({
  key: "detailBookSelector",
  get:
    (bookCode) =>
    ({ get }) => {
      const bookState = get(bookDetailState);
      return console.log(bookState);
    },
  set:
    (bookCode) =>
    ({ set, get }, newValue) => {
      const bookState = get(bookDetailState);
      const updatedData = bookState.map((item: any) => {
        if (item === newValue) {
          return { ...item, watch: !item.watch, isMarked: !item.isMarked };
        }
        return { ...item, watch: false, isMarked: false };
      });

      set(bookDetailState, updatedData);
    },
});
